import React, { useState } from 'react';


const App = ()=> {
    const purple = 'yellow'
    const   [bg, setBg] = useState(purple);
    const bgChange = ()=>{
        // console.log("clicked")
        let newBg = 'grey';
        setBg(newBg);
        
    
    }
    return(
        <>
        <div style = {{backgroundColor: bg}}>
            <button onClick= {bgChange}>
                Click Me
            </button>
        </div>

        </>
    )
}
export default App;
