import React, { useState } from 'react';


const App = ()=> {
    const purple = 'yellow'
    const   [bg, setBg] = useState(purple);
    const[name, setName] = useState('Click Me')
    const bgChange = ()=>{
        // console.log("clicked")
        let newBg = 'grey';
        setBg(newBg);
        setName("Ouch !! 🤸‍♀️")
        
    
    }
    const bgBack = ()=>{
        setBg(purple);
        setName("Ayyo !! 🥰")

    }
    return(
        <>
        <div style = {{backgroundColor: bg}}>
            <button onMouseEnter= {bgChange} onMouseLeave= {bgBack}>
                {name}
            </button>
        </div>

        </>
    )
}
export default App;
